const request = require('request');

const options = {
  method: 'DELETE',
  url: 'https://6-dot-authentiqio.appspot.com/key',
  qs: {
    email: 'SOME_STRING_VALUE',
    phone: 'SOME_STRING_VALUE',
    code: 'SOME_STRING_VALUE'
  }
};

request(options, function (error, response, body) {
  if (error) throw new Error(error);

  console.log(body);
});

import Foundation

let request = NSMutableURLRequest(url: NSURL(string: "https://6-dot-authentiqio.appspot.com/key?email=SOME_STRING_VALUE&phone=SOME_STRING_VALUE&code=SOME_STRING_VALUE")! as URL,
                                        cachePolicy: .useProtocolCachePolicy,
                                    timeoutInterval: 10.0)
request.httpMethod = "DELETE"

let session = URLSession.shared
let dataTask = session.dataTask(with: request as URLRequest, completionHandler: { (data, response, error) -> Void in
  if (error != nil) {
    print(error)
  } else {
    let httpResponse = response as? HTTPURLResponse
    print(httpResponse)
  }
})

dataTask.resume()

