import Foundation

let request = NSMutableURLRequest(url: NSURL(string: "https://ipgeolocation.abstractapi.com/v1/?api_key=SOME_STRING_VALUE&ip_address=195.154.25.40&fields=country%2Ccity%2Ctimezone")! as URL,
                                        cachePolicy: .useProtocolCachePolicy,
                                    timeoutInterval: 10.0)
request.httpMethod = "GET"

let session = URLSession.shared
let dataTask = session.dataTask(with: request as URLRequest, completionHandler: { (data, response, error) -> Void in
  if (error != nil) {
    print(error)
  } else {
    let httpResponse = response as? HTTPURLResponse
    print(httpResponse)
  }
})

dataTask.resume()
